import React, { useState, useRef, useEffect } from 'react';

function App() {
  return (
    <div className="accordion__main">
      <div className="accordion__title">
        <h1>FAQs</h1>
      </div>
      <div className="accordion">
        <AccordionItem title="What is Frontend Mentor, and how will it help me?">
          Frontend Mentor offers realistic coding challenges to help developers improve their frontend coding skills with projects in HTML, CSS, and JavaScript. It's suitable for all levels and ideal for anyone looking to practice and improve their coding skills.
        </AccordionItem>
        <AccordionItem title="Is Frontend Mentor free?">
          Yes, Frontend Mentor offers both free and premium challenges. Free challenges give you access to all the HTML and CSS, while premium challenges include more advanced projects with additional features.
        </AccordionItem>
        <AccordionItem title="Can I use Frontend Mentor projects in my portfolio?">
          Absolutely! The challenges are designed to help you create portfolio-worthy projects. You can use the completed projects to showcase your skills to potential employers.
        </AccordionItem>
        <AccordionItem title="How can I get help if I'm stuck on a challenge?">
          Frontend Mentor has an active community of developers who can help you. You can ask questions on the platform or join the Slack community to get advice and feedback from other members.
        </AccordionItem>
      </div>
    </div>
  );
}

function AccordionItem({ title, children }) {
  const [isOpen, setIsOpen] = useState(false);
  const contentRef = useRef(null);

  const toggleAccordion = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    if (isOpen) {
      contentRef.current.style.maxHeight = `${contentRef.current.scrollHeight}px`;
    } else {
      contentRef.current.style.maxHeight = '0px';
    }
  }, [isOpen]);

  return (
    <div className={`accordion__item ${isOpen ? 'open' : ''}`}>
      <div className="accrodion__header" onClick={toggleAccordion}>
        <p className="item__title">{title}</p>
        <p className="item__icon">
          {isOpen ? (
            <span className="minus">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="30"
                height="30"
                viewBox="0 0 30 30"
              >
                <path
                  fill="#301534"
                  d="M15 3.313A12.187 12.187 0 1 0 27.188 15.5 12.2 12.2 0 0 0 15 3.312Zm4.688 13.124h-9.376a.938.938 0 1 1 0-1.875h9.376a.938.938 0 1 1 0 1.875Z"
                />
              </svg>
            </span>
          ) : (
            <span className="plus">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="30"
                height="30"
                viewBox="0 0 30 30"
              >
                <path
                  fill="#301534"
                  d="M15 3.313A12.187 12.187 0 1 0 27.188 15.5 12.2 12.2 0 0 0 15 3.312Zm4.688 13.124h-9.376a.938.938 0 1 1 0-1.875h9.376a.938.938 0 1 1 0 1.875Z"
                />
              </svg>
            </span>
          )}
        </p>
      </div>
      <div
        ref={contentRef}
        className="accordion__content"
        style={{ maxHeight: isOpen ? `${contentRef.current.scrollHeight}px` : '0px' }}
      >
        <p>{children}</p>
      </div>
    </div>
  );
}

export default App;
